import React, { Component } from 'react';
import "bootstrap/dist/css/bootstrap.min.css";
import "./App.css";
import axios from "axios";

class App extends Component {
    constructor(){
        super()
        this.state = {
            fullName: '',
            username: '',
            email: '',
            password: ''
        }
        this.changeFullName = this.changeFullName.bind(this);
        this.changeEmail = this.changeEmail.bind(this);
        this.changeUsername = this.changeUsername.bind(this);
        this.changePassword = this.changePassword.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }
    changeFullName(event){
        this.setState({
            fullName: event.target.value
        })
    }
    changeUsername(event){
        this.setState({
            username: event.target.value
        })
    }
    changeEmail(event){
        this.setState({
            email: event.target.value
        })
    }
    changePassword(event){
        this.setState({
            password: event.target.value
        })
    }
    onSubmit(event){
        event.preventDefault()
        const registered = {
            fullName: this.state.fullName,
            username: this.state.username,
            email: this.state.email,
            password: this.state.password
        }
        axios.post('http://localhost:8000/api/signup', registered)
            .then(response => console.log(response.data))
        this.setState({
            fullName:'',
            username:'',
            email:'',
            password:''
        })       
    }

    render() {
        return ( 
            <div>
                <div className="container">
                    <div className="form-div">
                        <h1 style={{textAlign: 'center', marginTop:80}}>The SignUp Form</h1>
                        <form onSubmit={this.onSubmit} style={{ margin: 60, padding:20}}>
                            <input type="text" placeholder="Full Name" onChange={this.changeFullName} value={this.state.fullName} className="form-control form-group" style={{height:60}} />
                            <input type="text" placeholder="Username" onChange={this.changeUsername} value={this.state.username} className="form-control form-group" style={{height:60}}  />
                            <input type="text" placeholder="Email" onChange={this.changeEmail} value={this.state.email} className="form-control form-group" style={{height:60}}  />
                            <input type="password" placeholder="Password" onChange={this.changePassword} value={this.state.password} className="form-control form-group" style={{height:60}}  />
                            <input type="submit" className="btn btn-danger btn-block" value="Submit" style={{height:60}} />
                        </form>
                    </div>
                </div>

            </div>
        );
    }
}
export default App;
